import React, { useState, useEffect,useRef } from "react";
import HeaderExamine from "../components/HeaderExamine";
import { Container, Button } from "react-bootstrap";
import Examen from "../components/Examen";
import "./style/ExamPage.css";
import { useParams} from "react-router-dom";

const ExamPage = () => {
  const { id_examen, duree } = useParams();
  //manque soumission automatique
  //fait d'abord un post des réponses de l'examiné
  //correction automatique
  //la soummission se déclenche quand on clique sur soumettre oou si le temps est épuisé
  const [timer, setTimer] = useState(parseInt(duree) * 60);
  const startTimeRef = useRef(performance.now());

  useEffect(() => {
    const updateTimer = () => {
      // Calculate elapsed time since the timer started
      const elapsedTime = (performance.now() - startTimeRef.current) / 1000; // Convert to seconds
      // Calculate remaining time
      const remainingTime = Math.max(timer - elapsedTime, 0); // Ensure remaining time doesn't go negative
      // Update timer state
      setTimer(remainingTime);
      // Continue updating timer if remaining time is greater than 0
      if (remainingTime > 0) {
        requestAnimationFrame(updateTimer); // Schedule next update using requestAnimationFrame
      }
    };
    requestAnimationFrame(updateTimer);

    // Cleanup function
    return () => cancelAnimationFrame(updateTimer); // Cancel animation frame on component unmount
  }, [timer]);
    
    

  const formatTime = (seconds) => {
    const heures = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secondesRestantes = seconds % 60;
    return `${heures}:${String(minutes).padStart(2, "0")}:${String(secondesRestantes).padStart(2, "0")}`;
  };

  return (
    <div>
      <HeaderExamine />

      <div
        style={{
          backgroundColor: "rgba(142, 202, 230, 0.34)",
          minHeight: "100vh",
          padding: "20px 0",
        }}
      >
        <div className="sticky_container">
          <div className="timer">{formatTime(timer)}</div> <br />  
          <Button variant="danger">Finir et soumettre</Button>
        </div>

        <Container fluid className="contentContainer">
          <div className="Questions">
            <Examen id_examen={id_examen} />
          </div>
        </Container>
      </div>
    </div>
  );
};

export default ExamPage;
