import React, { useState, useEffect } from "react";
import QuestionExamen from './QuestionExamen'
import { useLocation } from 'react-router-dom';

const Examen = ({id_examen}) => {
  const [arrayQuestionId, setArrayQuestionId] = useState([]);
  const [reponsesQuestions, setReponsesQuestions] = useState([]); //contiendra les tuples (id_question, id_option) de toutes les questions
  const [timer, setTimer] = useState(0);

  const handleQuestion = (reponsesQuestion) => {
    setReponsesQuestions(prevReponsesQuestions => [...prevReponsesQuestions, ...reponsesQuestion]);
    console.log("voici toutes les réponses de l'examen", reponsesQuestion);
  };

  useEffect(() => {
    const storedTimer = localStorage.getItem('examTimer');
    if (storedTimer) {
      setTimer(parseInt(storedTimer));
    }
  }, []);
  useEffect(() => {
    localStorage.setItem('examTimer', timer.toString());
  }, [timer]);


  //chercher la liste des ids des questions de l'examen à partir de l'id de l'examen
  useEffect(() => {
    const getQuestionList = async () => {
      try {
        const response = await fetch(
          `http://localhost:3001/questions_examen/${id_examen}`
        );
        const jasonData = await response.json();
        setArrayQuestionId(jasonData);
      } catch (err) {
        console.error(err.message);
      }
    };
    getQuestionList();
  }, [id_examen]);



  return (
    <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-end" }}>
    {arrayQuestionId.map((questionId , index) => (
      <div key={index} style={{ marginTop: "10px", marginRight: "10px" }}>
        
        {console.log("Question ID:", questionId)}
        <QuestionExamen id_question={questionId.id} reponsesQuestionCallback={handleQuestion}/>
      </div>
    ))}
  </div>
 
  )
}

export default Examen
