import React, { useState, useEffect } from 'react';
import MenuExamine from './MenuExamine'
import NavbarProfil from './NavbarProfil'
import { useParams } from 'react-router-dom';

const HeaderExamine = () => {
  const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const { id }= useParams(); 

  
    useEffect(() => {
      const fetchUserData = async () => {
        try {
          const response = await fetch(`http://localhost:3001/utilisateur/${id}`);
          if (!response.ok) {
            throw new Error('Erreur de réseau ou réponse non valide');
          }
          const data = await response.json();
          setNom(data.nom);
          setPrenom(data.prenom);
        } catch (error) {
          console.error("Erreur lors de la récupération des données de l'utilisateur", error);
        }
      };
  
      if (id) {
        fetchUserData();
      }
    }, [id]);

    const urlLogo =`/Profile-examine/${id}`;
  return (
    <>
      <NavbarProfil  nom={nom} prenom={prenom} homePageURL={urlLogo} />
      <MenuExamine/>
    </>
  )
}

export default HeaderExamine
