import React, { useState, useEffect } from "react";
import QuestionExamen from "./QuestionExamen";
import { useLocation, useParams } from "react-router-dom";

const Examen = ({ id_examen, etat }) => {
  const{id}= useParams;
  const [arrayQuestionId, setArrayQuestionId] = useState([]);
  const [allResponses, setAllResponses] = useState({});
  const [soumis, setSoumis]= useState(false);

  useEffect(() => {
    const getQuestionList = async () => {
      try {
        const response = await fetch(
          `http://localhost:3001/questions_examen/${id_examen}`
        );
        const jsonData = await response.json();
        setArrayQuestionId(jsonData);
      } catch (err) {
        console.error(err.message);
      }
    };
    getQuestionList();
  }, [id_examen]);

  const handleUpdateResponse = (questionId, response) => {
    setAllResponses((prevResponses) => ({
      ...prevResponses,
       response,
    }));
    console.log("les réponses:", allResponses);
  };

  const handleSubmit = async () => {
    if (Object.keys(allResponses).length > 0) {
      try {
        const response = await fetch(
          `http://localhost:3001/Envoyer_reponses_examine/${id_examen}`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({ allResponses }),
          }
        );
        await response.json();
        console.log("réponses envoyées à la base de données!");
        localStorage.removeItem(`idReponses_${id_examen}`);
        window.location.href = `/Profile_examine/${id}`;
      } catch (error) {
        console.error("Erreur:", error);
      }
    } else {
      console.log("Aucune réponse à soumettre");
    }
  };

  useEffect(() => {
    if (etat && !soumis) {
      handleSubmit();
    }
  }, [etat, soumis]);

  return (
    <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-end" }}>
      {arrayQuestionId.map((questionId, index) => (
        <div key={index} style={{ marginTop: "10px", marginRight: "10px" }}>
          <QuestionExamen
            id_question={questionId.id}
            id_examen={id_examen}
            indice={index}
            etat={etat}
            onUpdateResponse={handleUpdateResponse}
          />
        </div>
      ))}
    </div>
  );
};

export default React.memo(Examen);
