import React, { useState } from "react";
import "../Pages/style/ReponseCreation.css";
import { Form, Table, Button } from "react-bootstrap";

const ReponseCreation = ({questionType}) => {
  const [options, setOptions] = useState(['', '']);

  const handleAjoutOption = () => {
    setOptions([...options, '']);
  };

  
  const handleConfirm = async () => {
    //ajout de la question et des options de réponses à la bdd
  }

  const handleAbondon = async () =>{
    //renvoie vers la page précédente
  }
  return (
    <>
      <div className="big_container">
        <div className="Question_container">
          <p>Intitulé de la réponse :</p>
          <Form.Control
            type="text"
            style={{ textAlign: "left" , marginBottom: "10px"}}
            placeholder="+ Ajouter Option de réponse"
            className="mb-3"
          />
          <Button style={{ marginBottom: "10px" }} onClick={handleAjoutOption}> Ajouter </Button>
        </div>

        <Table striped bordered hover className="table" style={{ marginTop: "10px" }}>
          <tbody>
            <tr>
              <td>checkbox or radio button</td>
              <td>texte de la réponse</td>
              <td>
                <Button variant="outline-danger">Supprimer</Button>
              </td>
            </tr>
          </tbody>
        </Table>
        <div className="Button_container">
      <Button variant="danger" onClick={handleAbondon}>Abandonner</Button>
      <Button variant="success" onClick={handleConfirm} >Confirmer la création de question</Button>
      </div>
      </div>
      
    </>
  );
};

export default ReponseCreation;
