import React, {useState, useEffect} from 'react'
import {Container  } from "react-bootstrap"; 
import ExamCard from '../components/ExamCard'
import { Link, useParams } from 'react-router-dom';
import HeaderExamine from "../components/HeaderExamine"


const Note = () => {
    const { id,id_examen } = useParams();
    const [noteExamen, setNoteExamen] = useState(null);
    const [titre, setTitre]= useState('');

    useEffect(() => {
        getNote();
        getTitre();
    }, []); 

    const getNote = async () => {
        try {
        const response = await fetch(`http://localhost:3001/ma_note/${id}/${id_examen}`);
        const jasonData = await response.json(); 
        setNoteExamen(jasonData);
        } catch (err) {
        console.error(err.message);
        }
    };

   const getTitre = async () => {
    try {
    const response = await fetch(`http://localhost:3001/examen/${id}/${id_examen}`);
    const jasonData = await response.json(); 
    setTitre(jasonData.titre);
    } catch (err) {
    console.error(err.message);
    }
};
  return (
    <div>
        <HeaderExamine/>
      <Container fluid className="contentContainer" style={{ backgroundColor: 'rgba(142, 202, 230, 0.34)', minHeight: '100vh', padding: '20px 0' }}>
      Votre note pour l'examen de : {titre}

      est {noteExamen}

      Mention : 

      </Container>
      
    </div>
  )
}

export default Note
