import React, { useEffect, useState } from "react";
import "../Pages/style/ReponseCreation.css";
import { Form, Table, Button } from "react-bootstrap";
import { useParams } from "react-router-dom";

const ReponseCrea = () => {
  const { id, id_examen, id_question, type } = useParams();

  const [description, setDescription] = useState("");
  const [valeur, setValeur] = useState("QCM");
  const [reponses, setReponses] = useState([]);

  // récupérer les réponses de la base de données #17
  const getReponses = async () => {
    try {
      const response = await fetch(
        `http://localhost:3001/reponses_possibles/${id_question}`
      );
      const responseData = await response.json();
      setReponses(responseData); // Mettre à jour les réponses
    } catch (err) {
      console.error(err.message);
    }
  };
  useEffect(() => {
    getReponses();
  }, []);

  // fonction pour ajouter une option de réponse
  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `http://localhost:3001/Ajouter_option_reponse/${id_question}`,
        {
          method: "POST",
          headers: {
            "Content-valeur": "application/json",
          },
          body: JSON.stringify({ description, valeur }),
        }
      );
      const responseData = await response.json();
      setReponses([...reponses, responseData]);
      setDescription("");
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="big_container">
      <div className="form_container">
        <h1>Intitulé de la réponse</h1>
        <Form onSubmit={onSubmitForm}>
          <Form.Control
            type="text"
            className="form-control"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <p>Indiquer si la réponse est :</p>
          <Form.Check
            type={'radio'}
            id={`default-${type}`}
            label={`Correcte`}
            className="form-check correct-radio"
          />
          <Form.Check
            type={'radio'}
            id={`default-${type}`}
            label={`incorrecte`}
            className="form-check incorrect-radio"
          />

          <Button type="submit" className="btn btn-primary">
            Ajouter
          </Button>
        </Form>
      </div>

      <div className="table_container">
        <Table bordered hover className="custom-table">
          <thead>
            <tr>
              <th>Valeur</th>
              <th>Description</th>
              <th>Supprimer</th>
            </tr>
          </thead>
          <tbody>
            {reponses.map((reponse) => (
              <tr key={reponse.id}>
                <td>valeur</td>
                <td>{reponse.texte}</td>
                <td>
                  <button className="btn btn-danger">Supprimer</button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>

      <div style={{ display: "flex", justifyContent: "center" }}>
        <Button variant="dark" size="lg">
          Enregistrer et quitter
        </Button>
      </div>
    </div>
  );
};

export default ReponseCrea;
