import React, { useState,useEffect } from "react";
import "../Pages/style/QuestionCreation.css";
import { Form,Button } from "react-bootstrap";
import { useParams } from 'react-router-dom';
import ReponseCreation from "./ReponseCrea";

const QuestionCreation = () => {
 const {id_question } = useParams(); //va m'indiquer si la question existe
 console.log("id_question=", id_question);
 const [infosQuestion,setInfosQuestion] =useState([]);

 const QuestionExiste = () => {
    //get toutes les informations d'une question si la question existe déjà en bdd
  const getInfosQuestion = async () => {
    try {
      const response = await fetch(`http://localhost:3001/question/${id_question}`);
      const jasonData = await response.json(); 
      setInfosQuestion(jasonData);
    } catch (err) {
      console.error(err.message);
    }
  };
  useEffect(() => {
    getInfosQuestion(); 
  }, [])

  if (infosQuestion.length > 0) {
  console.log("le nb points est ", infosQuestion[0].nb_points);
  const dureeString = infosQuestion[0].duree;
  const [heures, minutes, secondes] = dureeString.split(':');
  const totalMinutes = parseInt(heures) * 60 + parseInt(minutes);
  console.log("Total minutes:", totalMinutes);

  return (
    <><div className="big_container">
    <div className="flex_container">
      <div className="temps_container">
        Temps question :
        <Form.Control
          type="text"
          style={{ height: "30px", width: "110px", textAlign: "left" }}
          placeholder={totalMinutes }
        />
      </div>
      <div className="barème_container">
        Barème question :
        <Form.Control
          type="text"
          style={{ height: "30px", width: "110px", textAlign: "left" }}
          placeholder={infosQuestion[0].nb_points}
        />
      </div>
    </div>

    <div className="Question_container">
      <p>Intitulé de la question :</p>
      <textarea
        style={{ textAlign: "left" }}
        placeholder={infosQuestion[0].texte}
      />
    </div>

    <div className="question-type">
      Choisir le type de la question:
      <div className="custom-radio">
        <input type="radio" id="qcm" name="questionType" value="qcm" />
        <label htmlFor="qcm" data-label="QCM"></label>
      </div>
      <div className="custom-radio">
        <input type="radio" id="qcu" name="questionType" value="qcu" />
        <label htmlFor="qcu" data-label="QCU"></label>
      </div>
    </div>

    <div style={{ display: "flex", justifyContent: "center" }}>
      <Button variant="dark" size="lg">
        {" "}
        Enregistrer la question{" "}
      </Button>
    </div>
    {/*<ReponseCreation id_question={} type_question={}/>*/}
  </div>
  </>
  );
}else{
  return <div>Chargement...</div>;
}
};
 

const QuestionNExistePas = () => {
    return(
      <div className="big_container">
          <div className="flex_container">
            <div className="temps_container">
              Temps question :
              <Form.Control
                type="text"
                style={{ height: "30px", width: "110px", textAlign: "left" }}
                placeholder={"en minutes"}
              />
            </div>
            <div className="barème_container">
              Barème question :
              <Form.Control
                type="text"
                style={{ height: "30px", width: "110px", textAlign: "left" }}
                placeholder={"points"}
              />
            </div>
          </div>

          <div className="Question_container">
            <p>Intitulé de la question :</p>
            <textarea
              style={{ textAlign: "left" }}
              placeholder={"Texte de la question"}
            />
          </div>

          <div className="question-type">
            Choisir le type de la question:
            <div className="custom-radio">
              <input type="radio" id="qcm" name="questionType" value="qcm" />
              <label htmlFor="qcm" data-label="QCM"></label>
            </div>
            <div className="custom-radio">
              <input type="radio" id="qcu" name="questionType" value="qcu" />
              <label htmlFor="qcu" data-label="QCU"></label>
            </div>
          </div>

          <div style={{ display: "flex", justifyContent: "center" }}>
            <Button variant="dark" size="lg">
              {" "}
              Enregistrer la question{" "}
            </Button>
          </div>
          {/*<ReponseCreation id_question={} type_question={}/>*/}
        </div>
    );
};


  return (
    <div>
      {id_question ? QuestionExiste() : QuestionNExistePas()}
    </div>
        
  );
  
};

export default QuestionCreation;
