import React, { useState, useEffect } from "react";
import "../Pages/style/OptRepList.css";
import { useParams } from "react-router-dom";
import Form from "react-bootstrap/Form";

const OptRepList = ({ id_question, type_question, onUpdateResponse }) => {
  const { id } = useParams();
  const [arrayReponses, setArrayReponses] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleUpdateResponse = (responseId) => {
    if (type_question === 'QCU') {
      // For QCU, replace the selected option
      setSelectedOptions([responseId]);
    } else {
      // For QCM, toggle the selected option
      setSelectedOptions(prevOptions => {
        if (prevOptions.includes(responseId)) {
          return prevOptions.filter(option => option !== responseId);
        } else {
          return [...prevOptions, responseId];
        }
      });
    }
  };

  useEffect(() => {
    // Pass selected options back to parent component
    onUpdateResponse(id_question, selectedOptions);
  }, [selectedOptions]);

  // Function to fetch response options from the API based on question ID
  useEffect(() => {
    const getOptRep = async () => {
      try {
        const response = await fetch(`http://localhost:3001/reponses_possibles/${id_question}`);
        const jasonData = await response.json();
        if (Array.isArray(jasonData)) {
          setArrayReponses(jasonData);
        } else {
          console.error("No response from the API");
        }
      } catch (err) {
        console.error(err.message);
      }
    };

    getOptRep();
  }, [id_question]);

  return (
    <div className="Big_Container">
      <p className="consigne">Veuillez choisir la ou les r√©ponse(s) correcte(s) :</p>
      <div className="Smaller_Container">
        <Form>
          {arrayReponses.map((reponse) => (
            <div key={reponse.id} className="response_container">
              {type_question === 'QCM' ? (
                <div>
                  <input
                    type="checkbox"
                    id={reponse.id}
                    value={reponse.texte}
                    className="reponse"
                    onChange={() => handleUpdateResponse(reponse.id)}
                  />
                  <label htmlFor={reponse.id}>{reponse.texte}</label>
                </div>
              ) : (
                <div>
                  <input
                    type="radio"
                    id={reponse.id}
                    name="reponse"
                    value={reponse.texte}
                    className="reponseA"
                    onChange={() => handleUpdateResponse(reponse.id)}
                  />
                  <label htmlFor={reponse.id}>{reponse.texte}</label>
                </div>
              )}
            </div>
          ))}
        </Form>
      </div>
    </div>
  );
};

export default OptRepList;
