import React, { useState } from "react";
import "../Pages/style/QuestionCreation.css";
import { Form } from "react-bootstrap";

const QuestionCreation = () => {
  const[temps_q, setTemps_q]=useState('');
  const[bareme, setBareme]=useState('');
  const[texte_q, setTexte_q]=useState('');
  const[type_q, setType_q]=useState('');

  //ajouter des contraintes pour qu'on puisse valider que si tous les champs sont pleins et valides

  const handleTempsChange = (e) => {
    setTemps_q(e.target.value);
  };

  const handleBaremeChange = (e) => {
    setBareme(e.target.value);
  };

  const handleTexteChange = (e) => {
    setTexte_q(e.target.value);
  };

  const handleTypeChange = (event) => {
    setType_q(event.target.value);
  }
  
  
  return (
    <div className="big_container">
      <div className="flex_container">
        <div className="temps_container">
          Temps question :
          <Form.Control
            type="text"
            style={{ height: "30px", width: "110px", textAlign: "left" }}
            placeholder="en minutes"
            onChange={handleTempsChange}
          />
        </div>
        <div className="barème_container">
          Barème question :
          <Form.Control
            type="text"
            style={{ height: "30px", width: "110px", textAlign: "left" }}
            placeholder="points"
            onChange={handleBaremeChange}
          />
        </div>
      </div>
  
      <div className="Question_container">
        <p>Intitulé de la question :</p>
        <Form.Control
          type="text"
          style={{ height: "150px", textAlign: "left" }}
          placeholder="Texte de la question"
          onChange={handleTexteChange}
        />
      </div>

      <div class="radio-group">
          <p className="signup-label">Choisir le type de la question :</p>
          <input type="radio" id="examinator" name="role" value="QCM" className="input" onChange={handleTypeChange}/>
          <label for="examinator" class="radio">QCM</label>

          <input type="radio" id="examinee" name="role" value="QCU" className="input" onChange={handleTypeChange}/>
          <label for="examinee" class="radio">QCU</label>

          </div>

      
    </div>
   
  );
  
};

export default QuestionCreation;
