import React, { useState, useEffect } from "react";
import QuestionExamen from "./QuestionExamen";
import { useLocation, useParams } from "react-router-dom";

const Examen = ({ id_examen, etat }) => {
  const{id}= useParams();
  const [arrayQuestionId, setArrayQuestionId] = useState([]);
  const [allResponses, setAllResponses] = useState([]);
  const [soumis, setSoumis]= useState(false);

  useEffect(() => {
    const storedResponses = localStorage.getItem(`idReponses_${id_examen}`);
    if (storedResponses) {
      setAllResponses(JSON.parse(storedResponses));
    } else {
      setAllResponses([]); // Set initial state to an empty array if no stored responses
    }
  }, [id_examen]);

  useEffect(() => {
    const getQuestionList = async () => {
      try {
        const response = await fetch(
          `http://localhost:3001/questions_examen/${id_examen}`
        );
        const jsonData = await response.json();
        setArrayQuestionId(jsonData);
      } catch (err) {
        console.error(err.message);
      }
    };
    getQuestionList();
  }, [id_examen]);

  // Function to handle updating responses
const handleUpdateResponse = (questionId, responseId) => {
  // Filter out entries with empty arrays
  const filteredResponses = allResponses.filter(response => response.id_option.length > 0);
  
  // Find the index of the question in filteredResponses
  const questionIndex = filteredResponses.findIndex(response => response.id_question === questionId);

  if (questionIndex !== -1) {
    // Update existing response
    const updatedResponses = [...filteredResponses];
    updatedResponses[questionIndex].id_option = [responseId]; // Update id_option with selected option
    setAllResponses(updatedResponses);
  } else {
    // Add new response
    setAllResponses(prevResponses => [...prevResponses, { id_question: questionId, id_option: [responseId] }]);
  }
};

  

  useEffect(() => {
  console.log("voici les réponses sélectionnées", allResponses);
}, [allResponses]);

useEffect(() => {
  localStorage.setItem(`idReponses_${id_examen}`, JSON.stringify(allResponses));
}, [allResponses, id_examen]);

  const handleSubmit = async () => {
    
      try {
        const response = await fetch(
          `http://localhost:3001/Envoyer_reponses_examine/${id}/${id_examen}`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(allResponses),
          }
        );
        await response.json();
        console.log("réponses envoyées à la base de données!");
        //localStorage.removeItem(`idReponses_${id_examen}`);
        window.location.href = `/Profile_examine/${id}`;
      } catch (error) {
        console.error("Erreur:", error);
      }
    
  };

  useEffect(() => {
    if (etat && !soumis) {
      handleSubmit();
      // Update submitted state
      setSoumis(true);
    }
  }, [etat, soumis]);

  

  return (
    <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-end" }}>
      {arrayQuestionId.map((questionId, index) => (
        <div key={index} style={{ marginTop: "10px", marginRight: "10px" }}>
          <QuestionExamen
            id_question={questionId.id}
            id_examen={id_examen}
            indice={index}
            etat={etat}
            onUpdateResponse={handleUpdateResponse}

          />
        </div>
      ))}
    </div>
  );
};

export default React.memo(Examen);
