import React, { useEffect, useState } from "react";
import "../Pages/style/ReponseCreation.css";
import { Form, Table, Button } from "react-bootstrap";
import { Link, useParams } from "react-router-dom";
import ModalComponent from "./ModalComponent";

const ReponseCrea = () => {
  const { id, id_examen, id_question, type } = useParams();
  console.log(id_question);

  const [titre_O, setTitre_O] = useState(""); //texte de l'option de rép
  const [valeur_O, setvaleur_O] = useState("false"); // correcte ou incorrecte
  const [reponses, setReponses] = useState([]); //la liste des options de réponse
  const [rep, setRep] = useState(null); //1 seule option de rép

  const handleSelectChange = (e) => {
    setvaleur_O(e.target.value);
  };

  // récupérer les réponses de la base de données #17
  const getReponses = async () => {
    try {
      const response = await fetch(
        `http://localhost:3001/reponses_possibles/${id_question}`
      );
      const responseData = await response.json();
      setReponses(responseData); // Mettre à jour les réponses
    } catch (err) {
      console.error(err.message);
    }
  };
  useEffect(() => {
    getReponses();
  }, []);

  //renvoie le texte et vf d'une option de rép à partir de son id
  const getReponse = async (id_rep) => {
    try {
      const response = await fetch(
        `http://localhost:3001//option_reponse/${id_rep}`
      );
      const responseData = await response.json();
      setRep(responseData);
    } catch (err) {
      console.error(err.message);
    }
  };

  //supprimer une option de réponse
  const deleteOption = async (id_rep) => {
    try {
      const response = await fetch(
        `http://localhost:3001/Supprimer_option_reponse/${id_rep}`,
        {
          method: "DELETE",
        }
      );
      if (!response.ok) {
        throw new Error("Échec de la suppression de la réponse");
      }
      const jsonData = await response.json();
      console.log("Suppression réussie:", jsonData);
      setReponses(reponses.filter((r) => r.id !== id_rep));
    } catch (err) {
      console.error("Erreur lors de la suppression:", err);
    }
  };

  // fonction pour ajouter une option de réponse
  const onSubmitForm = async (e) => {
    e.preventDefault();
    console.log(titre_O, valeur_O);
    try {
      const response = await fetch(
        `http://localhost:3001/Ajouter_option_reponse/${id_question}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ titre_O, valeur_O }),
        }
      );
      console.log("post request réussi");
      const responseData = await response.json();
      console.log(responseData);
      setReponses([...reponses, responseData]);
      window.location.href = `/infos_reponses/${id}/${id_examen}/${id_question}/${type}`;
    } catch (error) {
      console.error("Error:", error);
    }
  };

  
  //mettre à jour une option de réponse
  const handleConfirm = async () => {
    try {
      const response = await fetch(
        `http://localhost:3001/Mettre_a_jour_titre_reponse/${rep.id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            texte: titre_O, 
            vf: valeur_O === "true", 
          }),
        }
      );
      if (!response.ok) {
        throw new Error("erreur de mise à jour");
      }
      console.log("réponse mise à jour avec succés");
      // Rafraîchir la liste des réponses après la modification
      getReponses();

    }catch (error){
      console.error(error);
    }  
  }

  return (
    <div className="big_container">
      <div className="form_container">
        <h1>Intitulé de la réponse</h1>
        <Form onSubmit={onSubmitForm}>
          <Form.Control
            type="text"
            className="form-control"
            value={titre_O}
            onChange={(e) => setTitre_O(e.target.value)}
          />
          <p>Indiquer si la réponse est :</p>
          <Form.Select
            aria-label="Default select example"
            onChange={handleSelectChange}
            value={valeur_O}
          >
            <option value="false">Incorrecte</option>
            <option value="true">Correcte</option>
          </Form.Select>

          <Button type="submit" className="btn btn-primary">
            Ajouter
          </Button>
        </Form>
      </div>

      <div className="table_container">
        <Table bordered hover className="custom-table">
          <thead>
            <tr>
              <th>valeur</th>
              <th>texte de la réponse</th>
              <th>Supprimer</th>
              <th>Modifier</th>
            </tr>
          </thead>
          <tbody>
            {reponses.map((reponse) => (
              <tr key={reponse.id}>
                <td
                  style={{
                    fontWeight: "bold",
                    color: reponse.vf ? "green" : "red",
                  }}
                >
                  {reponse.vf ? "correcte" : "incorrecte"}
                </td>
                <td>{reponse.texte}</td>
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => deleteOption(reponse.id)}
                  >
                    Supprimer
                  </button>
                </td>
                <td>
                  <ModalComponent
                    modalTitle="Modifier option de réponse"
                    buttonTitle="Modifier"
                    buttonStyle="warning"
                    closeText="Abandonner"
                    confirmText="confirmer"
                    handleConfirm={handleConfirm}
                    modalBody={
                      <Form onSubmit={handleConfirm}>
                        <Form.Control
                          type="text"
                          className="form-control"
                          value={titre_O || (rep ? rep.texte : "")}
                          onChange={(e) => setTitre_O(e.target.value)}
                        />

                        <Form.Select
                          aria-label="Default select example"
                          onChange={handleSelectChange}
                          value={valeur_O || (rep ? rep.vf.toString() : "false")}
                        >
                          <option value="true">Correcte</option>
                          <option value="false">Incorrecte</option>
                        </Form.Select>
                      </Form>
                    }
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>

      <div style={{ display: "flex", justifyContent: "center" }}>
        <Link to={`/creationquestion/${id}/${id_examen}/cree`}>
          <Button variant="dark" size="lg">
            Enregistrer et quitter
          </Button>
        </Link>
      </div>
    </div>
  );
};

export default ReponseCrea;
